css:
/* General */
.offer-letter {
  font-size: 16px;
  line-height: 1.6;
}
.mt-20 { margin-top: 20px; }

/* Logo */
.company-logo {
  text-align: center;
  margin-bottom: 10px;
}
.company-logo img {
  height: 120px;
  width: auto;
  border-radius: 5px;
  object-fit: cover;
}

/* Subject line */
.subject-line {
  text-align: center;
  font-size: 20px;
  font-weight: bold;
  margin: 20px 0;
}

/* Annexure Title */
.title {
  text-align: center;
  font-size: 20px;
  font-weight: bold;
  margin: 24px 0 10px;
}

/* CTC Table */
.ctc-wrap table       { width:100%; border-collapse:collapse; }
.ctc-wrap th,
.ctc-wrap td          { border:1px solid #333; padding:6px 8px; }
.ctc-wrap th          { text-align:left; }
.ctc-wrap td.r        { text-align:right; }
.ctc-wrap .hdr        { background:#e7f0fb; font-weight:bold; }
.ctc-wrap .section-a  { background:#eef4e8; font-weight:bold; }
.ctc-wrap .section-b  { background:#eef2ff; font-weight:bold; }
.ctc-wrap .section-c  { background:#fff0e6; font-weight:bold; }
.ctc-wrap .net        { background:#def3ff; font-weight:bold; }
.ctc-wrap .ctc        { background:#fff6cc; font-weight:bold; }

/* Signature */
.signature-block { margin-top: 20px; }
.signature-line {
  display: flex;
  align-items: center;
  margin-bottom: 10px;
}
.signature-label { width: 100px; }
.signature-input {
  display: inline-block;
  border-bottom: 1px solid #000;
  width: 250px;
  margin-left: 10px;
}

/* HR multicolor line */
hr.five-colors {
  border: 0;
  height: 2px;
  border-radius: 999px;
  background: linear-gradient(
    90deg,
    #e91e63 0 20%,
    #ff9800 20% 40%,
    #ffeb3b 40% 60%,
    #4caf50 60% 80%,
    #2196f3 80% 100%
  );
}
